using namespace std;

typedef long long ll;
typedef __float128 llf;
typedef pair<int, int> pp;

llf a, b, c;

vector<ll> fact(100);

llf sin(llf x) {
  llf r = 0, x2 = -x * x, f = 1;
  for (llf i = 1; i < 300; i += 2) {
    r += x / f;
    f *= (i + 1) * (i + 2);
    x *= x2;
  }
  return r;
}

llf cos(llf x) {
  llf r = 1, x2 = -x * x, f = 2;
  x = x2;
  for (llf i = 2; i < 300; i += 2) {
    r += x / f;
    f *= (i + 1) * (i + 2);
    x *= x2;
  }
  return r;
}

llf f(llf x) { return a * x + b * sin(x) - c; }
llf df(llf x) { return a + b * cos(x); }

int main() {
  llf x, nx, dx = 1e-25;
  llf e, ea = 1e-15;

  long double aa, bb, cc;
  cin >> aa >> bb >> cc;
  a = aa, b = bb, c = cc;

  x = c / a;
  do {
    nx = x - f(x) / df(x);
    e = abs(nx - x);
    x = nx;
  } while (e > ea);

  cout << fixed;
  cout.precision(6);
  cout << (long double)x;

  return 0;
}
